<?php/** * Piwik - free/libre analytics platform * * @link http://piwik.org * @license http://www.gnu.org/licenses/gpl-3.0.html GPL v3 or later */namespace Piwik\Plugins\MailingMonitoring;use Piwik\View;use Piwik\WidgetsList;use Piwik\Piwik;class Widgets extends \Piwik\Plugin\Widgets{    protected $category = 'General_Visitors';    protected function init()    {        $this->addWidget(            Piwik::translate('MailingMonitoring_BaseName') . ' - ' . Piwik::translate('MailingMonitoring_PPReport'),            'MailingMonitoringPPWidget'        );        $this->addWidget(            Piwik::translate('MailingMonitoring_BaseName') . ' - ' . Piwik::translate('MailingMonitoring_AIDReport'),            'MailingMonitoringAIDWidget'        );    }    public function MailingMonitoringPPWidget()    {        return Controller::MailingMonitoringDataTable(            'getPPReport',            array('label'=>'MailingMonitoring_PaywallPlan', 'value'=>'MailingMonitoring_Visitors')        );    }    public function MailingMonitoringAIDWidget()    {        return Controller::MailingMonitoringDataTable(            'getAIDReport',             array('label'=>'MailingMonitoring_ArticleID', 'value'=>'MailingMonitoring_Actions')        );    }}